import React, { useState } from "react";
import "./App.css";
import Aux from "./Auxiliary/Auxiliary";

function App() {
  const [count, func] = useState(0);
  const increment = () => {
    func(count + 1);
  };

  let newtime = new Date().toLocaleTimeString();
  let newtime2 = new Date().toLocaleTimeString();
  const [currenttime, settime] = useState(newtime);
  const [currenttime2, updatetime] = useState(newtime2);

  const time = () => {
    let newtime = new Date().toLocaleTimeString();
    settime(newtime);
  };
  const purple = "#8e44ad";
  const [bg, setBg] = useState(purple)

  const updatedtime = () => {
    let newtime2 = new Date().toLocaleTimeString();
    updatetime(newtime2);
  };

  setInterval(updatedtime, 1000);

  return (
    <Aux>
      <div className="counter">
        <h1> TIME: {currenttime2} </h1>
        <h1> {count} </h1>
        <h1>{newtime}</h1>
        <button onClick={increment}>CLICK ME</button>
        <button onClick={time}>GET TIME</button>
      </div>
    </Aux>
  );
}

export default App;
