import React, { useState } from "react";
import "./App.css";
import Aux from "./Auxiliary/Auxiliary";

function App() {
  const [count, func] = useState(0);
  const increment = () => {
    func(count + 1);
  };

  let newtime = new Date().toLocaleTimeString();
  let newtime2 = new Date().toLocaleTimeString();
  const [currenttime, settime] = useState(newtime);
  const [currenttime2, updatetime] = useState(newtime2);

  const time = () => {
    let newtime = new Date().toLocaleTimeString();
    settime(newtime);
  };
  const purple = "#8e44ad";
  const [bg, setBg] = useState(purple);

  const nam = "CLICK ME";
  const [name, setName] = useState(nam);



  const bgChange = () => {
    const newbg = "#34495e";
    setBg(newbg);
    setName("BOOM ðŸ’¥");

  }

const clicked = () => {
  setBg("pink");
  setName(" AHHHHHH ðŸ¥´")
}

  setInterval(time, 1000);

  return (
    <Aux>
      <div className="counter"  style = {{backgroundColor: bg}}>
        <h1> {count} </h1>
        <h1>{newtime}</h1>
        <button onClick={bgChange}  onDoubleClick = {clicked}>{name}</button>
        <button onClick={increment}>INCREMENT</button>
      </div>
    </Aux>
  );
}

export default App;
